-- Create "users" table
CREATE TABLE "users" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "email" character varying NOT NULL, "name" character varying NOT NULL, "preferences" jsonb NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, PRIMARY KEY ("id"));
-- Create index "users_email_key" to table: "users"
CREATE UNIQUE INDEX "users_email_key" ON "users" ("email");
-- Create "accounts" table
CREATE TABLE "accounts" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "description" character varying NULL, "type" character varying NOT NULL, "data" jsonb NOT NULL, "user_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "accounts_users_accounts" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "assets" table
CREATE TABLE "assets" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "symbol" character varying NOT NULL, "name" character varying NOT NULL, "type" character varying NOT NULL, "tags" jsonb NOT NULL, PRIMARY KEY ("id"));
-- Create index "asset_tags" to table: "assets"
CREATE INDEX "asset_tags" ON "assets" USING gin ("tags");
-- Create "portfolios" table
CREATE TABLE "portfolios" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "name" character varying NOT NULL, "description" character varying NULL, "data" jsonb NOT NULL, "user_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "portfolios_users_portfolios" FOREIGN KEY ("user_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "holdings" table
CREATE TABLE "holdings" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "amount" bigint NOT NULL, "decimals" bigint NOT NULL, "account_id" bigint NOT NULL, "asset_id" bigint NOT NULL, "portfolio_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "holdings_accounts_holdings" FOREIGN KEY ("account_id") REFERENCES "accounts" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "holdings_assets_holdings" FOREIGN KEY ("asset_id") REFERENCES "assets" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "holdings_portfolios_holdings" FOREIGN KEY ("portfolio_id") REFERENCES "portfolios" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create "prices" table
CREATE TABLE "prices" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "source_id" character varying NOT NULL, "interval" character varying NOT NULL, "decimals" bigint NOT NULL, "last" bigint NOT NULL, "open" bigint NULL, "high" bigint NULL, "low" bigint NULL, "close" bigint NULL, "volume" bigint NULL, "timestamp" timestamptz NOT NULL, "asset_id" bigint NOT NULL, "base_asset_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "prices_assets_prices" FOREIGN KEY ("asset_id") REFERENCES "assets" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "prices_assets_prices_base" FOREIGN KEY ("base_asset_id") REFERENCES "assets" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "price_asset_id_timestamp" to table: "prices"
CREATE UNIQUE INDEX "price_asset_id_timestamp" ON "prices" ("asset_id", "timestamp");
-- Create "transactions" table
CREATE TABLE "transactions" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "uuid" uuid NOT NULL, "type" character varying NOT NULL, "status" character varying NOT NULL, "data" jsonb NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "account_id" bigint NOT NULL, "asset_transactions" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "transactions_accounts_transactions" FOREIGN KEY ("account_id") REFERENCES "accounts" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "transactions_assets_transactions" FOREIGN KEY ("asset_transactions") REFERENCES "assets" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
